SELECT RESER_NO, MEMBER_NO, ROOM_NO, NON_NO, RESER_CHECKIN, RESER_CHECKOUT, RESER_PEOPLE, RESER_FOOD, RESER_STATUS, RESER_REQUEST
  FROM RESERVATION
 WHERE RESER_NO = #{reserNo}

SELECT PRICE_NO, TOTAL_PRICE, ROOM_PRICE, FOOD_PRICE, TIP_PRICE
  FROM PRICE
 WHERE PRICE_NO = #{priceNo}

SELECT RESER_NO, MEMBER_NO, ROOM_NO, NON_NO, RESER_CHECKIN, RESER_CHECKOUT, RESER_PEOPLE, RESER_FOOD, RESER_STATUS, RESER_REQUEST
  FROM RESERVATION
 WHERE MEMBER_NO = #{memberNo}
